package com.atguigu.gmall.manager.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.BaseCatalog1;import com.atguigu.gmall.service.Catalog1Service;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controllerpublic class BaseCatalog1Handler {    @Reference    Catalog1Service catalog1Service;    @RequestMapping("/getCatalog1")    @ResponseBody    public List<BaseCatalog1> getCatalog1(){        List<BaseCatalog1> catalog1s = catalog1Service.getCatalog1();        return catalog1s;    }}