package com.atguigu.gmall.manager.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.BaseCatalog2;import com.atguigu.gmall.service.Catalog2Service;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controllerpublic class BaseCatalog2Handler {    @Reference    Catalog2Service catalog2Service;    @RequestMapping(value="/getCatalog2")    @ResponseBody    public List<BaseCatalog2> getBaseCatalog2s(String catalog1Id){        List<BaseCatalog2> catalog2List = catalog2Service.getCatalog2s(catalog1Id);        return catalog2List;    }}