package com.atguigu.gmall.manager.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.BaseAttrInfo;import com.atguigu.gmall.bean.BaseCatalog3;import com.atguigu.gmall.service.BaseAttrInfoService;import com.atguigu.gmall.service.Catalog3Service;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controllerpublic class BaseCatalog3Handler {    @Reference    BaseAttrInfoService baseAttrInfoService;    @Reference    Catalog3Service catalog3Service;    @RequestMapping(value="/getCatalog3")    @ResponseBody    public List<BaseCatalog3> getCatalog3s(String catalog2Id){        List<BaseCatalog3> catalog3s = catalog3Service.getCatalog3s(catalog2Id);        return catalog3s;    }    @RequestMapping(value="/getAttrList")    @ResponseBody    public List<BaseAttrInfo> getAttrList(String ctg3Id){        List<BaseAttrInfo> attrInfos = baseAttrInfoService.selectAttrList(ctg3Id);        return attrInfos;    }}