package com.atguigu.gmall.manager.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.SkuImage;import com.atguigu.gmall.bean.SkuInfo;import com.atguigu.gmall.bean.SpuImage;import com.atguigu.gmall.service.SkuInfoService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;@Controllerpublic class SkuController {    @Reference    SkuInfoService skuInfoService;    @RequestMapping("skuList")    @ResponseBody    public List<SkuInfo> getSkuList(String spuId){        List<SkuInfo> skuInfoList = skuInfoService.getAll(spuId);        return skuInfoList;    }    @RequestMapping("saveSku")    @ResponseBody    public String getSkuList(SkuInfo skuInfo ){        skuInfoService.saveSkuInfo(skuInfo);        return "SUCCESS";    }}