package com.atguigu.gmall.manager.controller;import com.alibaba.dubbo.config.annotation.Reference;import com.atguigu.gmall.bean.BaseAttrInfo;import com.atguigu.gmall.bean.BaseSaleAttr;import com.atguigu.gmall.bean.SpuInfo;import com.atguigu.gmall.manager.util.MyUpladFile;import com.atguigu.gmall.service.BaseAttrInfoService;import com.atguigu.gmall.service.SpuInfoService;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.multipart.MultipartFile;import java.util.List;@Controllerpublic class SpuController {    @Reference    SpuInfoService spuInfoService;    @RequestMapping("/spuList")    @ResponseBody    public List<SpuInfo> getSpuList(String catalog3Id){        List<SpuInfo> spuInfos = spuInfoService.getSpuList(catalog3Id);        return spuInfos;    }    @RequestMapping("/baseSaleAttrList")    @ResponseBody    public List<BaseSaleAttr> getBaseSaleAttrList(){        List<BaseSaleAttr> baseSaleAttrList = spuInfoService.getBaseSaleAttrList();        return baseSaleAttrList;    }    @RequestMapping("/saveSpu")    @ResponseBody    public String saveSpu(SpuInfo spuInfo){        spuInfoService.saveSpuInfo(spuInfo);        return "SUCCESS";    }    @RequestMapping("/fileUpload")    @ResponseBody    public String fileUpload(@RequestParam("file")MultipartFile file){        String imgUrl = MyUpladFile.uploadImg(file);        return imgUrl;    }}