package com.atguigu.gmall.manager.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.BaseCatalog2;import com.atguigu.gmall.manager.mapper.Catalog2Mapper;import com.atguigu.gmall.service.Catalog2Service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestParam;import java.util.List;@Servicepublic class Catalog2ServiceImpl implements Catalog2Service {    @Autowired    private Catalog2Mapper catalog2Mapper;    @Override    public List<BaseCatalog2> getCatalog2s (String catalog1Id) {        BaseCatalog2 baseCatalog2 = new BaseCatalog2();        baseCatalog2.setCatalog1Id(catalog1Id);        List<BaseCatalog2> baseCatalog2s = catalog2Mapper.select(baseCatalog2);        return baseCatalog2s;    }}