package com.atguigu.gmall.manager.service.impl;import com.alibaba.dubbo.config.annotation.Service;import com.atguigu.gmall.bean.BaseCatalog3;import com.atguigu.gmall.manager.mapper.Catalog3Mapper;import com.atguigu.gmall.service.Catalog3Service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.ResponseStatus;import java.util.List;@Servicepublic class Catalog3ServiceImpl implements Catalog3Service {    @Autowired    Catalog3Mapper catalog3Mapper;    @Override    public List<BaseCatalog3> getCatalog3s (String ctg2id) {        BaseCatalog3 baseCatalog3 = new BaseCatalog3();        baseCatalog3.setCatalog2Id(ctg2id);        List<BaseCatalog3> catalog3s = catalog3Mapper.select(baseCatalog3);        return catalog3s;    }}